// src/components/StockMarket.js
import React, { useEffect, useState } from "react";

const StockMarket = () => {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    const fetchStockData = async () => {
      try {
        const response = await fetch("/api/stocks/");
        const data = await response.json();
        setStocks(Object.values(data).slice(0, 18)); // Limit to top 18 stocks
      } catch (error) {
        console.error("Error fetching stock data:", error);
      }
    };

    fetchStockData();
  }, []);

  return (
    <div style={styles.container}>
      <h1>Stock Market</h1>
      <div style={styles.stockList}>
        {stocks.map((stock, index) => (
          <div key={index} style={styles.stockItem}>
            <h2>{stock.name}</h2>
            <p>Price: ${stock.price}</p>
            <p style={{ color: stock.change >= 0 ? "green" : "red" }}>
              Change: {stock.change}%
            </p>
            <div style={styles.buttonContainer}>
              <button style={styles.buyButton}>Buy</button>
              <button style={styles.sellButton}>Sell</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: { padding: "20px", textAlign: "center" },
  stockList: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
  stockItem: {
    border: "1px solid #ddd",
    padding: "20px",
    margin: "10px",
    width: "150px",
    textAlign: "center",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  buttonContainer: {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "10px",
  },
  buyButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "5px 10px",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  sellButton: {
    backgroundColor: "#d9534f",
    color: "white",
    padding: "5px 10px",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};

export default StockMarket;
