// src/components/StockMarket.js
import React, { useEffect, useState } from "react";

const StockMarket = () => {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    const fetchStockData = async () => {
      try {
        const response = await fetch("/api/stocks/"); // Ensure this is the correct endpoint
        const data = await response.json();
        setStocks(Object.values(data)); // Assuming data is an object of stocks
      } catch (error) {
        console.error("Error fetching stock data:", error);
      }
    };

    fetchStockData();
  }, []);

  return (
    <div style={styles.container}>
      <h1>Stock Market</h1>
      <div style={styles.stockList}>
        {stocks.map((stock, index) => (
          <div key={index} style={styles.stockItem}>
            <h2>{stock.name}</h2>
            <p>Price: ${stock.price}</p>
            <p>Change: ${stock.change}</p>
          </div>
        ))}
      </div>
      <div>
        <button style={styles.buySellButton}>Buy/Sell</button>
      </div>
    </div>
  );
};

const styles = {
  container: { padding: "20px" },
  stockList: {
    display: "flex",
    justifyContent: "space-around",
    flexWrap: "wrap",
  },
  stockItem: {
    border: "1px solid #ddd",
    padding: "20px",
    width: "200px",
    textAlign: "center",
    margin: "10px",
  },
  buySellButton: {
    padding: "10px",
    margin: "5px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
};

export default StockMarket;
