// src/components/SignInCreateAccount.js
import React, { useState } from 'react';
import axios from 'axios';

const SignInCreateAccount = () => {
  const [isSigningIn, setIsSigningIn] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSignIn = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/sign-in/', { email, password });
      alert(response.data.message);
    } catch (error) {
      alert(error.response.data.error);
    }
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Passwords don't match!");
      return;
    }
    try {
      const response = await axios.post('http://localhost:8000/api/create-account/', { email, password });
      alert(response.data.message);
      setIsSigningIn(true); // Switch to Sign In mode after account creation
    } catch (error) {
      alert(error.response.data.error);
    }
  };

  return (
    <div>
      <button onClick={() => setIsSigningIn(true)}>Sign In</button>
      <button onClick={() => setIsSigningIn(false)}>Create Account</button>

      {isSigningIn ? (
        <form onSubmit={handleSignIn}>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button type="submit">Sign In</button>
        </form>
      ) : (
        <form onSubmit={handleCreateAccount}>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
          <button type="submit">Create Account</button>
        </form>
      )}
    </div>
  );
};

export default SignInCreateAccount;
