// src/components/Dashboard.js
import React, {useEffect, useState } from 'react';

const Dashboard = () => {
  // State to store the stock data
  const [stocks, setStocks] = useState({});

   // Fetch stock data from backend
   useEffect(() => {
    const fetchStockData = async () => {
      try {
        const response = await fetch("/api/stocks/"); // Make sure this matches your Django URL
        const data = await response.json();
        setStocks(data);
      } catch (error) {
        console.error("Error fetching stock data:", error);
      }
    };

    fetchStockData();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.section}>Portfolio Value: $0.00</h2>
      <p>Portfolio Gain: $0.00</p>

      <div style={styles.section}>
        <h3>Stocks</h3>
        {Object.entries(stocks).map(([symbol, stock]) => (
          <div key={symbol}>
            <h4>{stock.name} ({symbol})</h4>
            <p>Price: ${stock.price}</p>
            <p>Change: ${stock.change} ({stock.changePercent}%)</p>
          </div>
        ))}
      </div>

      <div style={styles.navigation}>
        <button style={styles.button}>Buy/Sell Stocks</button>
        <button style={styles.button}>Predictions</button>
        <button style={styles.button}>Learn</button>
      </div>
    </div>
  );
};

const styles = {
  container: { padding: '20px', textAlign: 'center' },
  section: { margin: '20px 0' },
  navigation: { display: 'flex', justifyContent: 'space-around' },
  button: { padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' },
};

export default Dashboard;
